class Solution {
public: 
    int numberOfAlternatingGroups(vector<int>& hues, int target) { 
        int N = hues.size(); 
        int tally = 0; 

        if (target == 1) { 
            return N; 
        } 

        int streak = 1; 
        int i = 1; 
        while (i < N + target - 1) { 
            int now = i; 
            int then = i - 1; 

            if (now >= N) { 
                now -= N; 
            } 
            if (then >= N) { 
                then -= N; 
            } 

            if (hues[now] != hues[then]) { 
                ++streak; 
            } else { 
                streak = 1; 
            } 

            if (streak >= target) { 
                ++tally; 
            } 

            ++i; 
        } 

        return tally; 
    } 
};
