class Solution {
public:
    int numberOfAlternatingGroups(vector<int>& colors, int k) {
        vector<int> v;
        for (auto i:colors) v.push_back(i);
        for (auto i:colors) v.push_back(i);
        int n=v.size();
        vector<int> pref;
       // for (auto i:v) cout<<i<<" ";
       // cout<<endl;
        for (int i=1;i<n;i++) {
            if (v[i]==v[i-1]) pref.push_back(1);
            else pref.push_back(0);
        }
        for (int i=1;i<n;i++) pref[i]+=pref[i-1];
        int c=0;
        for (int i=0;i<colors.size();i++) {
            //cout<<i<<" "<<i+k-1<<" "<<pref[i]<<" "<<pref[i+k-1]<<endl;
            if (pref[i+k-1]==pref[i]) c++;
        }
        return c;
    }
};
