class Solution {
public:
    static const int maxn = 1e5+10;
    int precnt[maxn<<1];
    int numberOfAlternatingGroups(vector<int>& colors, int k) {
        int n = colors.size();
        for(int i = 0;i<n;++i){
            colors.push_back(colors[i]);
        }
        for(int i = 1;i<colors.size();++i){
            precnt[i] = precnt[i-1] + (colors[i] == colors[i-1]);
        }
        int res = 0;
        for(int i = 0;i<n;++i){
            res += (precnt[i+k] - precnt[i + 1]) == 0;
        }
        return res;
    }
};
