class Solution {
public:
    int numberOfAlternatingGroups(vector<int>& colors , int k) {
        int groups = 0, cur = 1 , n = colors.size();

        for(int i = 0 ; i < colors.size() + k - 2; i++){
            if(colors[(i) % n] != colors[ (i-1+n) % n]){

                cur++;
            } else{
                cur = 1;
            }

            if(cur >= k){
                groups++;
            }
        }

        return groups;
    }
};
