class Solution {
public:
    int numberOfAlternatingGroups(vector<int>& colors, int k) {
        int N=colors.size();
        int cnt=0;
        if(k==1){
            return N;
        }
        int alternating_length=1;
        int i=1;
        while(i<N+k-1){
            int curr=i;
            int pre=i-1;
            if(curr>=N){
                curr-=N;
            }
            if(pre>=N){
                pre-=N;
            }
            if(colors[curr]!=colors[pre]){
                alternating_length++;
            }
            else{
                alternating_length=1;
            }
            if(alternating_length>=k){
                cnt++;
            }
            i++;
        }
        return cnt;
    }
};
