class Solution:
    def numberOfAlternatingGroups(self, colors: List[int], k: int) -> int:
        n = len(colors)
        colors = colors * 2
        res = 0
        cur = 1
        for i in range(1, n + k - 1):
            if colors[i] != colors[i - 1]:
                cur += 1
                if cur >= k:
                    res += 1
            else:
                cur = 1
        return res
