class Solution {
public:
    int numberOfAlternatingGroups(vector<int>& colors, int k) {
        int n = colors.size();
        if (n < k) return 0;

        vector<int> numes(colors.begin(), colors.end());

        numes.insert(numes.end(), colors.begin(), colors.begin() + k - 1);

        int cntu = 0;
        int aC = 0;

        for (int i = 1; i < k; ++i) {
            if (numes[i] != numes[i - 1]) {
                aC++;
            }
        }
        if (aC == k - 1) {
            cntu++;
        }

        for (int i = 1; i < n; i++) {
            if (numes[i - 1] != numes[i]) {
                aC--;
            }
            if (numes[i + k - 1] != numes[i + k - 2]) {
                aC++;
            }
            if (aC == k - 1) {
                cntu++;
            }
        }

        return cntu;
    }
};
