class Solution:
    def numberOfAlternatingGroups(self, colors: List[int], k: int) -> int:
        n = len(colors)
        cpy = [el for el in colors]
        colors += cpy
        cnt = 0
        bef = colors[0]
        cur = 1
        for i in range(1, n+k-1):
            if colors[i] != bef:
                cur += 1
                bef = colors[i]
            else:
                cur = 1
            if cur >= k:
                cnt += 1
        return cnt
