class Solution
{
    bool f = true;
    void upd(vector<int> &c, int k, int n)
    {
        loop(i, 1, n) dp[i] += dp[i - 1] + abs(c[i] - c[i - 1]);
    }

    void check(vector<int> &c, int k)
    {
        c.insert(c.end(), c.begin(), c.begin() + k);
    }
    vi dp;
    mpi mp;

public:
    int numberOfAlternatingGroups(vector<int> &c, int k)
    {
        check(c, k);
        int n = c.size(), ans = 0;
        dp.resize(n + 1, 0);
        upd(c, k, n);
        k--;
        loop(i, k + 1, n) ans += (dp[i] - dp[i - k] == k);
        return ans;
    }
};
