class Solution {
public:
    int numberOfAlternatingGroups(vector<int>& colors, int k) {
        int n=colors.size();
        vector<int> v=colors;
        for(int i=0;i<k-1;i++)
        v.push_back(colors[i]);
        int cnt=0;
       // int i=0;
        //int j=0;
        n=v.size();
        int j=0;
        for(int i=1;i<n;i++)
        {
              if(v[i]==v[i-1])
              {
                   int x=i-j;
                   if(x>=k)
                   {
                       cnt+=x-k+1;
                   }
                   j=i;
              }
        }
        int x=(n-j);
         if(x>=k)
        {
          cnt+=x-k+1;
         }
        return cnt;
    }
};
