class Solution {
    public int numberOfAlternatingGroups(int[] ar, int k) {
        ArrayList<Integer> list = new ArrayList<>();
        int count = 1;
        int n = ar.length;
        for(int i=0;i<n-1;i++){
            if(ar[i]!=ar[(i+1)%n]){
                count++;
            }else{
                list.add(count);
                count = 1;
            }
        }
        if(ar[n-1]!=ar[0]){
            if(list.size()==0){
                return n;
            }else{
                 list.add(list.get(0)+count);
            list.remove(0);
            }
        }else{
            list.add(count);
        }
        // System.out.println(list);
        long ans = 0;

        for(Integer a : list){
            if(a>k-1)
            ans += a-k+1;
        }
        return (int)ans;
    }
}
