class Solution {
public:
    int numberOfAlternatingGroups(vector<int>& colors, int k) {
        int n = colors.size();
        for(int i = 0; i < n - 1; i++) {
            colors.push_back(colors[i]);
        }
        vector<int> p(n * 2 - 1);
        for(int i = 1; i < n * 2 - 1; i++) {
            if(colors[i] != colors[i-1]) {
                p[i] = p[i-1];
            } else {
                p[i] = i;
            }
        }

        int cnt = 0;
        for(int i = n - 1; i < 2 * n - 1; i++) {
            if(i - p[i] + 1 >= k) cnt++;
        }
        return cnt;
    }
};
