class Solution {
public:
    int numberOfAlternatingGroups(vector<int>& c, int k) {
        int n = c.size();
        int l = 0, ans = 0;
        for (int i = 0; i < n; ++i)
            c.push_back(c[i]);
        for (int i = 0; i < n; ++i)
            c.push_back(c[i]);

        for (int i = 0; i < n+k-1; ++i) {
            if (i && c[i]==c[i-1])
                l = i;

            if (i-l+1==k) {
                //cout << l << " " << i << endl;
                ++ans;
                ++l;
            }
        }

        return ans;
    }
};
