class Solution {
public:
    int numberOfAlternatingGroups(vector<int>& a, int k) {
        int n=a.size(),i,l,r,ans=0,tot;
        vector<int> b(n),pre(n);
        for(i=0;i<n;i++)
        {
            if(a[i]!=a[(i-1+n)%n] && a[i]!=a[(i+1)%n])
                b[i]=1;
        }
        pre[0]=b[0];
        for(i=1;i<n;i++)
            pre[i]=pre[i-1]+b[i];
        for(i=0;i<n;i++)
        {
            l=i,r=i+k-1;
            if(r-1<n)
            {
                tot=pre[r-1]-pre[l];
                if(tot==k-2)
                    ans++;
            }
            else
            {
                tot=pre[n-1]-pre[l];
                r%=n;
                tot+=pre[r-1];
                if(tot==k-2)
                    ans++;
            }
            //cout<<l<<" "<<r<<" "<<ans<<endl;
        }
        return ans;
    }
};
