class Solution {
public:
  int numberOfAlternatingGroups(vector<int>& colors, int k) {
    int n = colors.size(), res = 0;
    vector<int> c;
    for(auto &it : colors) c.push_back(it);
    for(auto &it : colors) c.push_back(it);

    int s = 0, e = 1;
    while(e < 2 * n) {
      if(c[e] == c[e - 1]) {
        s = e;
      } else {
        if(e - s + 1 > k) {
          s++;
        }
        if(s >= n) break;
        if(e - s + 1 == k) {
          res++;
        }
      }
      e++;
    }
    return res;
  }
};
