class Solution {
public:
    int numberOfAlternatingGroups(vector<int>& c, int k) {

        int n = c.size();

        vector<int> temp = c;

        for(int i = 0; i<k-1 ; i++){
            temp.push_back(c[i]);
        }

        int l = 0, r=0;

        int m = temp.size();

        int pre = -1;

        // int sz = 0;
        int ans = 0;

        for(int i = 0 ; i<m ; i++){
            if(pre!=temp[i]){
                pre = temp[i];
                if(i-l+1 == k){
                    ans+=1;
                }else if(i-l+1>k){
                    if(k%2 == 0 && temp[l+1]!=temp[i]){
                        ans+=1;
                        l++;
                    }else if(k%2 == 1 && temp[l+1] == temp[i]){
                        ans+=1;
                        l++;
                    }
                }
            }else{
                l = i;
            }
        }

        return ans;


    }
};
