class Solution {
public:
    int numberOfAlternatingGroups(vector<int>& a, int k) {
        int x=a.size();
        for(int i=0;i<k;++i){
            a.push_back(a[i]);
        }
        int n=a.size();
        int pref[n];
        pref[0]=1;
        for(int i=1;i<n;++i){
            if(a[i]!=a[i-1])
                pref[i]=pref[i-1]+1;
            else 
                pref[i]=1;
        }
        int ans=0;
        for(int i=0;i<x;++i){
            int r=i+k-1;
            if((pref[r]-pref[i])==(k-1)) ans++;
        }
        return ans;
    }
};
