class Solution {
public:
    int numberOfAlternatingGroups(vector<int>& colors, int k) {
        int res = 0;
        int n = colors.size();
        for(int i = 0; i < n; i ++) {
            colors.push_back(colors[i]);
        }
        int dp = 1;
        for(int i = 2 * n - 2; i >= n; i --) {
            if(colors[i] != colors[i + 1]) {
                dp ++;
            }
            else {
                dp = 1;
            }
        }
        for(int i = n - 1; i >= 0; i --) {
            if(colors[i] != colors[i + 1]) {
                dp ++;
            }
            else {
                dp = 1;
            }
            res += dp >= k;
        }
        return res;
    }
};
