class Solution {
public:
    int numberOfAlternatingGroups(vector<int>& colors, int k) {
        int n = colors.size();
        int start = 0;
        int ret = 0;
        bool pre = false;

        while (start < n)
        {
            //cout<<start<<endl;
            int end = (start + k - 1 + n) % n;//闭区间
            int mid = (start + 1)%n;
            int count = 0;
            if (pre)
            {
                count = k-3;
                mid = (end - 1 + n) % n;
            }
            for (; count<k-2; mid=(mid+1)%n, count++)
            {
                int left = (mid - 1 + n) % n;
                int right = (mid + 1 + n) % n;
                if (colors[mid] == colors[left] || colors[mid] == colors[right])
                {
                    break;
                }
            }
            if (count >= k-2)
            {
                ret++;
                pre = true;
                start++;
            }
            else
            {
                pre = false;
                if (mid > start)
                {
                    start = mid;
                }
                else
                {
                    start = n;
                }
            }
        }
        return ret;
    }
};
