class Solution:
    def numberOfAlternatingGroups(self, colors: List[int], k: int) -> int:
        ans = 0
        n = len(colors)
        colors = colors + colors[ : n]
        i = 0
        while i < n:
            l = i 
            i += 1
            while i < n + k - 1 and colors[i] != colors[i - 1]:
                i += 1
            if i - l >= k:
                ans += i - l - k + 1
        return ans
