class Solution {
public:
    int numberOfAlternatingGroups(vector<int>& a, int k) {
        for(int i=0; i<k - 1; ++i) a.push_back(a[i]);
        int n = a.size();
        int cur = 1;
        int ret = 0;
        for(int i=1; i<n; ++i){
            if(a[i] != a[i-1]){
                ++cur;
            } else {
                cur = 1;
            }
            if(cur >= k)    ++ret;
        }
        return ret;
    }
};
