class Solution {
 public:
  int numberOfAlternatingGroups(vector<int>& colors, int k) {
    for (int i = 0; i < k - 1; ++i) {
      colors.push_back(colors[i]);
    }
    int N   = colors.size();
    int ret = 0;
    vector<int> dp(N);
    dp[0] = 1;
    for (int i = 1; i < N; ++i) {
      if (colors[i] != colors[i - 1]) {
        dp[i] = dp[i - 1] + 1;
      } else {
        dp[i] = 1;
      }
      if (dp[i] >= k) ret++;
    }
    return ret;
  }
};
