class Solution:
    def numberOfAlternatingGroups(self, colors: List[int], k: int) -> int:
        arr = colors + colors[:k - 1]
        l = 0
        ans = 0
        for i, x in enumerate(arr):
            if i > 0 and x == arr[i - 1]:
                l = i
            if i - l + 1 >= k:
               ans += 1
        return ans
