class Solution {
public:
    int numberOfAlternatingGroups(vector<int>& v, int k) {
        for(int i=0;i<k-1 ;i++){
            v.push_back(v[i]);
        }
        set<int> st;
        int n=v.size();
        for(int i=1;i<n;i++){
            if(v[i]==v[i-1])st.insert(i);
        }
        int ans=0;
        for(int i=0;i<n-k+1;i++)
        {
            auto val=st.upper_bound(i);
            if(val==st.end()){
                ans++;
                continue;
            }

            int ind=*val;
            if(ind>i+k-1)ans++;
        }
        return ans;
    }
};
