class Solution {
public:
    int numberOfAlternatingGroups(vector<int>& colors, int k) 
    {
        vector<int> v = colors;
        for(int i = 0; i < k-1; i++)
        {
            v.push_back(colors[i]);
        }
        int n = v.size(), cnt = 1, ans = 0;
        for(int i = n-1; i >= 1; i--)
        {
            if(v[i] == v[i-1])
                cnt = 1;
            else
            {
                cnt++;
                if(cnt == k)
                    ans++, cnt--;
            }
        }
        return ans;

    }
};
