class Solution {
public:
  #define ll long long 
  int numberOfAlternatingGroups(vector<int>& a, int k) {
    ll n = a.size();
    vector<ll> v(n, 1);
    for(ll i = 1; i < n; i++) 
    {
      if(a[i] != a[i - 1]) 
      {
        v[i] = 1 + v[i - 1];
      }
    }
    ll as = 0;
    for(ll i = 0; i < n; i++) 
    {
      if(i + k - 1 < n && v[i + k - 1] >= k)as++;
      else 
      {
        ll j = (i + k - 1) % n;
        if(a[n - 1] != a[0] && v[n - 1] >= n - i &&  v[j] == j + 1)as++;
      }
    }
    return as;
  }
};
