class Solution {
public:
    int numberOfAlternatingGroups(vector<int>& colors, int k) {
        for(int i=0;i<k-1;i++){
            colors.push_back(colors[i]);
        }

        int i=0, j=0;
        int count = 0;
        vector<int> v;
        while(j<colors.size()-1){
            if((colors[j]^colors[j+1])==1){
                j++;
            }else{
                v.push_back(j-i+1);
                j++;
                i=j;
            }
        }
        v.push_back(j-i+1);

        int ans= 0;

        for(int i=0;i<v.size();i++){
            if(v[i]>=k){
                ans=ans+v[i]-k+1;
            }
        }

        return ans;
    }
};
