class Solution {
public:
    int numberOfAlternatingGroups(vector<int>& arr, int k) {
        int cnt=1;
        int n=arr.size(),ans=0;
        for(int i=0;i<n;i++)arr.push_back(arr[i]);
    //  for(auto i:arr)cout<<i<<" ";
        for(int i=1;i<n+k-1;i++){
            if(arr[i]==arr[i-1]){
                cnt=1;
            }
            else{
                cnt++;
                if(cnt>=k){
                    ans++;
                }
            }
        }
        return ans;
    }
};
