class Solution {
public:
    int numberOfAlternatingGroups(vector<int>& colors, int k) {
        vector<int> a;
        for (int b = 0; b < colors.size(); b++) {
            a.push_back(colors[b]);
        }
        for (int c = 0; c < k - 1; c++) {
            a.push_back(colors[c]);
        }

        int d = 0;
        int e = 0;
        while (e < a.size()) {
            int f = 1;
            int g = e + 1;
            while (g < a.size()) {
                if (a[g] != a[g - 1]) {
                    f++;
                } else {
                    e = g - 1;
                    break;
                }
                if (g == a.size() - 1) {
                    e = 3 * a.size();
                    break;
                }
                g++;
            }
            d = d+max(0, f - k + 1);
            e=e+1;
        }

        return d;
    }
};
