class Solution {
public:
    int numberOfAlternatingGroups(vector<int>& colors, int k) {
        int n=colors.size();
        int left=0,right=1;
        int ans=0;
        while(right<n){
            if(colors[right]!=colors[right-1]){
                right++;
                if(right-left==k){
                    ans++;
                    left++;
                }
            }else{
                left=right;
                right++;
            }
        }
        // cout<<left<<"; "<<right<<"\n";
        right=0;
        // if(n%2 || k!=n){
            while(left<n){
                // cout<<right%n<<" - "<<(n+right-1)%n<<"\n";
                if(colors[right%n]!=colors[(n+right-1)%n]){
                    right++;
                    // cout<<right+(n-left)<<"\n";
                    if(right+(n-left)==k){
                        ans++;
                        left++;
                    }
                }else{
                    left=right;
                    right++;
                }
            }
        // }
        return ans;
    }
};
