class Solution { 
public: 
    int numberOfAlternatingGroups(vector<int>& colors, int k) { 
        int N = colors.size(); 
        int count = 0; 

        if (k == 1)         
        return N; 


        int alter = 1; 
        int i = 1; 
        while (i < N + k - 1) 
        { 
            int current = i; 
            int prev = i - 1; 

            if (current >= N)
            current -= N; 
            if (prev >= N)  
            prev -= N; 

            if (colors[current] != colors[prev]) 
            ++alter; 
            else 
            alter = 1; 


            if (alter >= k) 
            ++count; 

            ++i; 
        } 

        return count; 
    } 
};
