class Solution {
public:
    int numberOfAlternatingGroups(vector<int>& colors, int k) {
        int n = colors.size();
        vector<int> t(n+5, 0);
        for(int i = 0; i < n; i++) {
            if(colors[(i - 1 + n) % n] != colors[i] && colors[(i + 1 + n) % n] != colors[i]) t[i] = 1;
            // cout << t[i] << ' ';
        }
        int res = 0;
        int index = 0;
        while(index < n && t[index] == 1) index++;
        if(index == n) {
            return n;
        }
        for(int i = index + 1; i != index; i = (i + 1) % n) {
            if(t[i] == 0) continue;
            int r = i;
            int cnt = 0;
            while(r != index && t[r] == 1) {
                cnt++;
                t[r] = 0;
                r = (r + 1) % n;
            }
            i = r;
            if(cnt + 2 >= k)
                res += (cnt - k + 2 + 1);
        }
        return res;
    }
};
