class Solution {
public:
    int numberOfAlternatingGroups(vector<int>& a, int k) {
        int n = a.size();
        int ans = 0;
        int ls = n + k-1,j=0,pre = -1,len=0;
        for(int i=0;i<ls;i++){
            if(a[j]!=pre){
                len++;
                if(len>=k)ans++;
                pre = a[j];

            }
            else{
                pre = a[j];
                len = 1;

            }

                j++;
                j%=n;

        }
        return ans;
    }
};
