class Solution {
public:
    int numberOfAlternatingGroups(vector<int>& colors, int k) {
        int n = colors.size();
        int ans = 0;
        int cnt = 1;
        cout<<n+k-2<<"\n";
        for(int i=1;i<=n+k-2;i++){
            if(colors[i%n]!=colors[(i-1+n)%n]){
                cnt++;
            }
            else{
                cnt = 1;
            }
            if(cnt>=k)ans++;
        }

        return ans;
    }
};
