class Solution {
public:
    int numberOfAlternatingGroups(vector<int>& colors, int k) {
        int n = colors.size();
        for(int i = 0;i < n;i++) colors.push_back(colors[i]);

        vector<int> isDiff;
        for(int i = 0;i + 1 < 2*n;i++) isDiff.push_back(colors[i] != colors[i + 1]);

        int cnt = 0, sum = 0;
        for(int l = 0,r = 0;l < n;r++){
            sum += isDiff[r];
            if(r - l + 1 == k - 1){
                cnt += (r - l + 1 == sum);
                sum -= isDiff[l];
                l++;
            }
        }

        return cnt;
    }
};
