class Solution { 
public: 
    int numberOfAlternatingGroups(vector<int>& colors, int k) { 
        int n = colors.size(); 
        int cnt = 0; 

        if (k == 1) { 
            return n; 
        } 

        int altLength = 1; 
        int i = 1; 
        while (i < n+k-1) { 
            int curr = i; 
            int prev = i - 1; 


            if (curr >= n) { 
                curr = curr-n; 
            } 
            if (prev >= n) { 
                prev = prev- n; 
            } 

            if (colors[curr] != colors[prev]) { 
                altLength++; 
            } else { 
                altLength = 1; 
            } 

            if (altLength >= k) { 
               cnt++; 
            } 

           i++; 
        } 

        return cnt; 
    } 
};
