class Solution {
public:
    int numberOfAlternatingGroups(vector<int>& a, int k) {
        a.insert(a.end(), a.begin(), a.begin() + k - 1);
        int n = a.size(), res = 0;
        for(int i = 0; i < n; i++) {
            int len = 1;
            while(i + 1 < n and a[i + 1] != a[i]) i++, len++;
            res += max(0, len - k + 1);
        }
        return res;
    }
};
