class Solution:
    def numberOfAlternatingGroups(self, arr: List[int], k: int) -> int:
        n=len(arr)
        gg=[]
        for i in range(n):
            if arr[i]^arr[(i+1)%n]: gg.append(1)
            else: gg.append(0)
        pre_sum={-1:0}
        for i in range(n): pre_sum[i]=pre_sum[i-1]+gg[i]
        ans=0
        for i in range(n):
            j=i+k-2
            if j<n:
                if pre_sum[j]-pre_sum[i-1]==k-1: ans+=1
                continue
            if pre_sum[n-1]-pre_sum[i-1]+pre_sum[j%n]==k-1: ans+=1
        return ans
