class Solution {
    public int numberOfAlternatingGroups(int[] colors, int k) {

        int n = colors.length,res =0 , ans = 1;


        if (k == 1) return n;

        int i = 1;

        while (i < n + k - 1) {

            int crr = i;
            int prvs = i - 1;


            if (crr >= n) crr -= n;


            if (prvs >= n) prvs -= n;


            if (colors[crr] != colors[prvs]) ans++;
            else ans =1;          

            if (ans>= k) res++;

            ++i;
        }


        return res;



    }
}
