class Solution:
    def numberOfAlternatingGroups(self, colors: List[int], k: int) -> int:
        last=None
        ends=[0]
        n=len(colors)
        colors=colors*2
        for i,e in enumerate(colors):
            if e==last:
                ends.append(i)
            last=e
        ends.append(len(colors))
        #print(ends)

        return sum(max(0,min(a,n+k-1)-e-k+1 if min(e,a)<n else 0) for e,a in zip(ends,ends[1:]))
