class Solution {
public:
    int numberOfAlternatingGroups(vector<int>& colors, int k) {
        int n = colors.size(), i;
        for(int i=0 ; i<n ; i++) colors.push_back(colors[i]);

        set <int> st;
        int ret = 0;

        for(i=1 ; i<colors.size() ; i++){
            if(colors[i]==colors[i-1]) st.insert(i);
        }

        for(i=0 ; i<n ; i++){
            auto it = st.lower_bound(i+1);
            if(it==st.end()){
                ret++;
            }
            else if(*it>=i+k) ret++;
        }

        return ret;

    }
};
