#include <vector>
using namespace std;

class Solution {
public:
    int numberOfAlternatingGroups(vector<int>& aryan1, int aryan2) {
        int aryan3 = aryan1.size();
        int aryan4 = 0;

        if (aryan2 == 1) {
            return aryan3;
        }

        int aryan5 = 1;
        int aryan6 = 1;

        while (aryan5 < aryan3 + aryan2 - 1) {
            int aryan7 = aryan5;
            int aryan8 = aryan5 - 1;

            if (aryan7 >= aryan3) {
                aryan7 -= aryan3;
            }
            if (aryan8 >= aryan3) {
                aryan8 -= aryan3;
            }

            if (aryan1[aryan7] != aryan1[aryan8]) {
                ++aryan6;
            } else {
                aryan6 = 1;
            }

            if (aryan6 >= aryan2) {
                ++aryan4;
            }

            ++aryan5;
        }

        return aryan4;
    }
};
