class Solution {
    public int numberOfAlternatingGroups(int[] cs, int k) {
        int n=cs.length;
        int res=0;
        int[] arr=new int[n+k-1];
        for(int i=0;i<n;i++){
            arr[i]=cs[i];
        }
        for(int i=n;i<n+k-1;i++){
            arr[i]=cs[i-n];
        }
        boolean bool=false;
        for(int i=0;i<n+k-1;i++){
            if(bool){
              //System.out.println(i);  
              if(arr[i]!=arr[i-1]){
                 res++;
              }else{
                bool=false;
              }
            }
            if(!bool){
               int j=i;
                while(j+1<n+k-1&&arr[j+1]!=arr[j]){
                   j++;
                   if(j-i+1==k) break;  
                }
                //System.out.println(i+","+j);
                if(j-i+1==k){
                    res++;
                    bool=true;
                    i=j;
                }else{
                    i=j;
                }

            }

        }

        return res;

    }
}
