class Solution {
    public int numberOfAlternatingGroups(int[] colors, int k) {
        int n = colors.length;
        int[] arr = new int[n * 2];
        for (int i = 0; i < n; i++) {
            arr[i] = colors[i];
            arr[i + n] = colors[i];
        }
        int ans = 0;
        int pre = 0;
        for (int i = 1; i < k - 1; i++) {
            if (arr[i] == arr[i - 1]) {
                pre = i;
            }
        }
        for (int i = k - 1; i <= n + k - 2; i++) {
            if (arr[i] == arr[i - 1]) {
                pre = i;
            }
            if (i - pre + 1 >= k) {
                ans++;
            }
        }
        return ans;
    }
}
