class Solution {
public:
    int numberOfAlternatingGroups(vector<int>& colors, int k) {
        int ans = 0, n = colors.size();
        for(int i = 0; i < n; ++i) {
            colors.push_back(colors[i]);
        }
        for(int l = 0, r = 1; l < n;) {
            while(r < 2 * n && r - l + 1 < k && colors[r] != colors[r-1] && colors[r] != colors[r+1]) {
                ++r;
            }
            if(r-l+1 == k) ans++, ++l;
            else l = r, r += 1;
        }
        return ans;
    }
};
