class Solution {
public:
    int numberOfAlternatingGroups(vector<int>& c, int k) {
        int n=c.size();
        vector<int> v;
        for(int i=n-(k-1);i<=n-1;i++) v.push_back(c[i]);
        for(auto&i:c) v.push_back(i);
        // for(int i=0;i<k-2;i++) v.push_back(c[i]);
        int sz=0,ans=0;
        for(int i=1;i<v.size()-1;i++){
            if(v[i]!=v[i-1] && v[i]!=v[i+1]) sz++;
            else sz=0;
            if(sz>=(k-2)) ans++;
        }
        return ans;
    }
};
