class Solution {
public:
    int numberOfAlternatingGroups(vector<int>& colors, int k) {


        auto modify = [&](){

           colors.insert(colors.end(),  colors.begin(), colors.begin()+k-1);

        };

        modify();

        int ans = 0, temp = 1,n = colors.size();

        for(int i=1;i<n;i++){

          if(colors[i] != colors[i-1]) temp++;
            else temp  = 1;

            ans+=(temp>=k);
        }



      return ans;  

    }
};
