#include <bits/stdc++.h>
using namespace std;
#define ll long long
#define ld long double
#define all(x) (x).begin(), (x).end()
#define INF 2e18
#define vi  vector<int>
#define vb  vector<bool>
#define vc  vector<char>
#define vvi vector<vector<int>>
#define vvc vector<vector<char>> 
#define vvb vector<vector<bool>> 
#define vpi vector<pair<int,int>>
#define ff first
#define ss second
#define pb push_back
#define ppb pop_back
#define pqb priority_queue<int>
#define pqs priority_queue<int,vi,greater<int> >
#define setbits(x) __builtin_popcountll(x)

class Solution {
public:
    int numberOfAlternatingGroups(vector<int>& arr, int k) {
        int n=arr.size();
        vector<int> ds;
        for(int i=0;i<n;i++){
            if(arr[i]==0)ds.pb(-1);
            else ds.pb(1);
        }

        for(int i=0;i<k-1;i++){
            if(arr[i]==0)ds.pb(-1);
            else ds.pb(1);            
        }
        int l=ds.size();
        // for(auto &it : ds)cout<<it<<" ";
        // cout<<endl;
        vector<int> lmao;
        for(int i=0;i<l-1;i++){
            lmao.pb(ds[i]+ds[i+1]);
        }
        // for(auto &it : lmao)cout<<it<<" ";
        // cout<<endl;
        k--;
        // k size subarray all 0
        l=lmao.size();
        map<int,int> mpp;
        for(int i=0;i<k;i++){
            mpp[lmao[i]]++;
        }
        int ans=0;
        if(mpp.size()==1 && (*mpp.begin()).ff==0)ans++;
        for(int i=k;i<l;i++){
            mpp[lmao[i]]++;
            mpp[lmao[i-k]]--;
            if(mpp[lmao[i-k]]==0)mpp.erase(lmao[i-k]);

            if(mpp.size()==1 && (*mpp.begin()).ff==0)ans++;
        }
        return ans;
    }
};
