class Solution {
public:
    int numberOfAlternatingGroups(vector<int>&a, int k) {
        int n=a.size();

        vector<int>x;

        if(a[0]==a[n-1]) x.push_back(0);

        for(int i=1;i<n;i++)
            if(a[i]==a[i-1]) x.push_back(i);

        if(x.size()==0) return n;

        vector<int>lens;

        int m=x.size();


        for(int i=1;i<m;i++)
            lens.push_back(x[i]-x[i-1]);

        lens.push_back(n+x[0]-x[m-1]);

        int ans=0;

        for(auto y : lens)
        {
            if(y<k) continue;
            ans+=(y-k+1);
        }

        return ans;
    }
};
