class Solution:
    def numberOfAlternatingGroups(self, colors: List[int], k: int) -> int:
        def get_index(i, n):
            return i % n

        n = len(colors)
        group_count = 1
        result = 0

        for i in range(1, n + k - 1):
            current_index = get_index(i, n)
            previous_index = get_index(i - 1 + n, n)

            if colors[current_index] != colors[previous_index]:
                group_count += 1
            else:
                group_count = 1

            if group_count >= k:
                result += 1

        return result
